commits.content:
@font-face {
    font-family: "Fraunces";
    src: url("/Users/trenttuedon/Desktop/workit-landing-page/starter-code/assets/fonts/fraunces/Fraunces-VariableFont_SOFT,WONK,opsz,wght.ttf"); 

}

@font-face {
    font-family: "Manrope";
    src: url("starter-code/assets/fonts/manrope/Manrope-VariableFont_wght.ttf"); 

}

* {
    margin: 0;
    padding: 0;
}

body {
    background: linear-gradient(180deg, rgba(36,5,62,1) 0%, rgba(252,248,255,1) 30%, rgba(255,255,255,1) 100%); 
    width: 1100px;
}


/* Showcase */
.container {
    max-width: 1100px;
    margin:  auto;
}

.overlay {
    width: 1500px;
    max-width: 1500px;
    height: 730px;
    background-color: #24053e;
    border-radius: 100% /0 0 80px 80px; 
}
.navbar {
    color: #fff;
    display: flex;
    justify-content: space-between;
    position: relative;
    top: 50px;
}

.navbar a {
    text-decoration: none;
    color: #fff;
}

.secondary-default {
    font-family: sans-serif;
    border-bottom: 3px solid #44ffa1;
}

.secondary-default:hover {
    color: #44ffa1;
}

.bg-pattern-1 {
   position: absolute;
   left: -140px;
   top: 140px;
}

.bg-pattern-2 {
    position: absolute;
    left: 1330px;
    top: 250px;
 }

.heading {
    justify-content: center; 
    width: 635px;
    height: 258px;
    margin: 0 auto;
    margin-top: 146px;
    text-align: center;
}

.heading h1 {
    font-family: Fraunces;
    color: #fff;
    font-weight: 600;
    line-height: 80px;
    margin-bottom: 40px;
    font-size: 80px;
}

.underline {
    text-decoration: underline;
    text-decoration-thickness: 2px;
    text-decoration-color: #44ffa1;
    text-underline-offset: 6px;
}

.primary-default {
    font-family: sans-serif;
    line-height: 40px;
    background-color: #44ffa1;
    border: 0;
    color: black;
    text-decoration: none;
    padding: 20px 40px;
}

.primary-default:hover {
    background-color: #24053e;
    color: #44ffa1;
    border: 1px solid #44ffa1;
}

.image-hero {
    display: block;
    margin-top: 30px;
    width: 700px;
    height: 400px;
}

/* Features */
.grid-container {
    display: grid; 
    padding-top: 200px;
    grid-template-rows: 1fr 1fr;
    grid-template-columns: 1fr 1fr 1fr;
    font-family: "Fraunces";
    gap: 30px;
    text-align: center;
}


.feature-num {
    border: 1px solid #584d62;
    border-radius: 50%;
    width: 20px;
    height: 20px;
    padding: 10px;
    margin: auto;
    text-align: center;
    margin-bottom: 50px;
    color: #24053e;
}


.purple-overlay {
    background: #fdf8ff;
    border-radius: 0 0 50% 50% / 50px;
    width: 1500px;
    height: 600px;

}

.feature-heading {
    margin-bottom: 40px;
    font-weight: 600;
    color: #24053e;
}

.feature-paragraph {
    font-family: "Manrope";
    font-size: 18px;
    line-height: 32px;
    width: 300px;
    text-align: center;
    font-weight: 400;
    color: #584d62;
    margin: auto;

}


/* About Section */

.image-founder {
    margin-left: 250px;
}

.about{
    position: relative;
    color: #fff;
    background-color: #24053e;
    width: 730px;
    height: 413px;
    bottom: 200px;
    left: 400px;
    margin-left: 100px;
}

.about-container {
    text-align: left;
    padding: 70px;
}

.about-container h3 {
    font-family: "Fraunces";
    font-size: 56px;
    font-weight: 600;
    margin-bottom: 32px;
}

.about-container p {
    font-family: "Manrope";
    font-weight: 400;
    font-size: 18px;
    line-height: 32px;
    margin-bottom: 30px;
}

.about-container a {
    margin-top: 100px;
}

.bg-pattern-3 {
    position: relative;
    bottom: 120px;
    left: 400px;
}

/* Footer */

.spacing {
    margin-top: 100px;
}

footer {
    margin-left: 300px;
    text-align: center;
    padding-bottom: 100px;
}

.icons {
    margin-top: 40px;

}

.icon {
    padding: 0 20px 0 20px;
}


/* Media Queries */

@media (min-width: 768px) and (max-width: 1024px) { 

    .body {
        background-color: black;
    }
    .bg-pattern-1 {
        left: -200px;
    }

    .bg-pattern-2 { 
        left: 1330px;
        overflow: hidden;
    }
    .grid-container {
        display: grid;
        position: relative;
        grid-template-columns: none;
        grid-template-rows: repeat(3, 1fr);
        margin-top: 200px;
        padding: 20px;
        gap: 100px;
        text-align: left;
    }
    .feature-heading {
       text-align: left;
       margin-left: 300px;
       margin-bottom: 20px;
    }
    .feature-num {
        position: absolute;
        left: 250px;
        margin-top: 0 auto;

    }

    .feature-paragraph {
        text-align: left;
        width: 493px;
        height: 56px;
    }

}
